class NewsScreen extends StatelessWidget {
  final HomeBloc homeBloc;
  NewsScreen({Key key, this.homeBloc}) : super(key: key);
  final RefreshController _refreshController = RefreshController();

  @override
  Widget build(BuildContext context) {
    return BlocBuilder<HomeBloc, HomeState>(
        bloc: homeBloc,
        builder: (
          BuildContext context,
          HomeState currentState,
        ) {
          if (currentState is UnHomeState) {
            return Center(
              child: SpinKitChasingDots(
                color: Tools.multiColors[Random().nextInt(3)],
              ),
            );
          }
          if (currentState is ErrorHomeState) {
            return Container(
                padding: const EdgeInsets.all(16.0),
                child: Center(
                  child: Text(
                    currentState.errorMessage ?? 'Error',
                    textAlign: TextAlign.center,
                  ),
                ));
          }

          var state = homeBloc.currentState as InHomeState;
          var news = state.newsData.news;
          var newsList = news.where((s) => s.n_id != 0).toList();

          return SmartRefresher(
              controller: _refreshController,
              enablePullDown: true,
              // header: defaultHeader,
              onRefresh: () async {
                print("Pulled down");
                homeBloc.dispatch(LoadHomeEvent());

                /*  var events = state.eventsData.events;
          eventSessions = events.where((s) => s.e_state == false).toList(); */
                _refreshController.refreshCompleted();
              },
              child: buildlist(
                  newsList, context) //EventList( allEvents: eventSessions)

              );
        });
  }

  Widget buildlist(var newsList, context) {
    return ListView.builder(
      shrinkWrap: false,
      itemCount: newsList.length,
      itemBuilder: (c, i) {
        // return Text("sdd");
        return Card(
          elevation: 0.0,
          child: ListTile(
            onTap: () {
              Navigator.push(
                context,
                MaterialPageRoute(
                  builder: (context) => NewsDetail(
                    news: newsList[i],
                  ),
                ),
              );
            },
            // dense: true,
            isThreeLine: true,
            trailing: Icon(FontAwesomeIcons.newspaper,color: Colors.green,), 
            /* Container(
                height: 100,
                width: 100,
                child: CachedNetworkImage(
                    imageUrl: //"${newsList[i].n_image}",
                        "https://img.etimg.com/thumb/msid-68333505,width-643,imgsize-204154,resizemode-4/googlechrome.jpg")) */
            /*  Image.network(
                  "https://img.etimg.com/thumb/msid-68333505,width-643,imgsize-204154,resizemode-4/googlechrome.jpg"),
            ), */
            /*     trailing:Container(
              
              child: CachedNetworkImage(
                
        imageUrl: "data:image/png;base64,",
        placeholder: (context, url) => new CircularProgressIndicator(),
        errorWidget: (context, url, error) => new Icon(Icons.error),
     ),
            ), */

            /* leading: Hero(
              tag: "${newsList[i].n_author}\n",
              child: CircleAvatar(
                radius: 30,
                backgroundImage: CachedNetworkImageProvider(
                    'https://avatars1.githubusercontent.com/u/12619420?s=400&u=eac38b075e4e4463edfb0f0a8972825cf7803d4c&v=4'),
              ),
