            title: Text(
              "${newsList[i].n_title}",
              style: Theme.of(context).textTheme.title.copyWith(
                    fontSize: 18.0,
                    fontWeight: FontWeight.bold,
                    
                  ),
            ),
            subtitle: Text(
              "\n${newsList[i].n_author}\n${newsList[i].n_datetime.toString()}",
              
              style: Theme.of(context).textTheme.caption.copyWith(
